        }

        .action-button.complete:hover {
          background: #059669;
        }

        .completed-text {
          color: #10b981;
          font-size: 12px;
          font-weight: 500;
        }

        .chat-section,
        .profile-section {
          padding: 20px;
        }

        .profile-card {
