import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { useAuth } from '../../context/AuthContext';
import { ownerAPI, handleApiError } from '../../services/api';
import { ArrowLeft, Phone, Shield, Clock } from 'lucide-react';

const OwnerLogin = () => {
  const [step, setStep] = useState(1); // 1: phone, 2: code
  const [phoneNumber, setPhoneNumber] = useState('');
  const [accessCode, setAccessCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [timeLeft, setTimeLeft] = useState(0);

  const { login, isAuthenticated } = useAuth();
  const navigate = useNavigate();

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/owner/dashboard', { replace: true });
    }
  }, [isAuthenticated, navigate]);

  // Countdown timer for code expiry
  useEffect(() => {
    let timer;
    if (timeLeft > 0) {
      timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    }
    return () => clearTimeout(timer);
  }, [timeLeft]);

  const formatPhoneNumber = (value) => {
    // Remove all non-digit characters
    const digits = value.replace(/\D/g, '');

    // Format as (XXX) XXX-XXXX
    if (digits.length <= 3) {
      return digits;
    } else if (digits.length <= 6) {
      return `(${digits.slice(0, 3)}) ${digits.slice(3)}`;
    } else {
      return `(${digits.slice(0, 3)}) ${digits.slice(3, 6)}-${digits.slice(6, 10)}`;
    }
  };

  const handlePhoneSubmit = async (e) => {
    e.preventDefault();

    if (!phoneNumber.trim()) {
      toast.error('Please enter your phone number');
      return;
    }

    // Convert formatted phone to digits only
    const digitsOnly = phoneNumber.replace(/\D/g, '');

    if (digitsOnly.length < 10) {
      toast.error('Please enter a valid 10-digit phone number');
      return;
    }

    // Add country code if not present
    const formattedPhone = digitsOnly.length === 10 ? `+1${digitsOnly}` : `+${digitsOnly}`;

    setLoading(true);

    try {
      const response = await ownerAPI.createAccessCode(formattedPhone);

      if (response.data.success) {
        toast.success('Access code sent to your phone!');
        setStep(2);
        setTimeLeft(600); // 10 minutes

        // Show access code in development
        if (process.env.NODE_ENV === 'development' && response.data.accessCode) {
          toast.info(`Development: Access code is ${response.data.accessCode}`);
        }
      }
    } catch (error) {
      const errorMessage = handleApiError(error);
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleCodeSubmit = async (e) => {
    e.preventDefault();

    if (!accessCode.trim()) {
      toast.error('Please enter the access code');
      return;
    }

    if (accessCode.length !== 6) {
      toast.error('Access code must be 6 digits');
      return;
    }

    const digitsOnly = phoneNumber.replace(/\D/g, '');
    const formattedPhone = digitsOnly.length === 10 ? `+1${digitsOnly}` : `+${digitsOnly}`;

    setLoading(true);

    try {
      const response = await ownerAPI.validateAccessCode(formattedPhone, accessCode);

      if (response.data.success) {
        const { token, user } = response.data;
        login(token, user);
        toast.success('Login successful! Welcome back.');
        navigate('/owner/dashboard');
      }
    } catch (error) {
      const errorMessage = handleApiError(error);
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleResendCode = async () => {
    if (timeLeft > 0) {
      toast.warning('Please wait before requesting a new code');
      return;
    }

    const digitsOnly = phoneNumber.replace(/\D/g, '');
    const formattedPhone = digitsOnly.length === 10 ? `+1${digitsOnly}` : `+${digitsOnly}`;

    setLoading(true);

    try {
      const response = await ownerAPI.createAccessCode(formattedPhone);

      if (response.data.success) {
        toast.success('New access code sent!');
        setAccessCode('');
        setTimeLeft(600);

        if (process.env.NODE_ENV === 'development' && response.data.accessCode) {
          toast.info(`Development: Access code is ${response.data.accessCode}`);
        }
      }
    } catch (error) {
      const errorMessage = handleApiError(error);
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  return (
    <div className="login-page">
      <div className="login-container">
        {/* Header */}
        <div className="login-header">
          <Link to="/" className="back-link">
            <ArrowLeft size={20} />
            Back to Home
          </Link>
        </div>

        {/* Login Card */}
        <div className="login-card">
          <div className="login-card-header">
            <h1>Manager Login</h1>
            <p>Access your team management dashboard</p>
          </div>

          {step === 1 ? (
            /* Step 1: Phone Number */
            <form onSubmit={handlePhoneSubmit} className="login-form">
              <div className="step-indicator">
                <div className="step active">
                  <Phone size={20} />
                </div>
                <div className="step-line"></div>
                <div className="step">
                  <Shield size={20} />
                </div>
              </div>

              <h2>Enter Your Phone Number</h2>
              <p className="step-description">
                We'll send you a 6-digit verification code via SMS
              </p>

              <div className="form-group">
                <label htmlFor="phoneNumber" className="form-label">
                  Phone Number
                </label>
                <input
                  type="tel"
                  id="phoneNumber"
                  className="form-input"
                  placeholder="(555) 123-4567"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(formatPhoneNumber(e.target.value))}
                  maxLength={14}
                  required
                />
              </div>

              <button 
                type="submit" 
                className="btn btn-primary btn-lg"
                disabled={loading}
                style={{ width: '100%' }}
              >
                {loading ? (
                  <>
                    <div className="loading-spinner"></div>
                    Sending Code...
                  </>
                ) : (
                  'Send Verification Code'
                )}
              </button>

              <div className="login-help">
                <p>
                  New to Skipli? Contact your system administrator to get started.
                </p>
              </div>
            </form>
          ) : (
            /* Step 2: Access Code */
            <form onSubmit={handleCodeSubmit} className="login-form">
              <div className="step-indicator">
                <div className="step completed">
                  <Phone size={20} />
                </div>
                <div className="step-line completed"></div>
                <div className="step active">
                  <Shield size={20} />
                </div>
              </div>

              <h2>Enter Verification Code</h2>
              <p className="step-description">
                We sent a 6-digit code to {phoneNumber}
              </p>

              <div className="form-group">
                <label htmlFor="accessCode" className="form-label">
                  Verification Code
                </label>
                <input
                  type="text"
                  id="accessCode"
                  className="form-input code-input"
                  placeholder="123456"
                  value={accessCode}
                  onChange={(e) => setAccessCode(e.target.value.replace(/\D/g, '').slice(0, 6))}
                  maxLength={6}
                  required
                />
              </div>

              {timeLeft > 0 && (
                <div className="code-timer">
                  <Clock size={16} />
                  Code expires in {formatTime(timeLeft)}
                </div>
              )}

              <button 
                type="submit" 
                className="btn btn-primary btn-lg"
                disabled={loading}
                style={{ width: '100%' }}
              >
                {loading ? (
                  <>
                    <div className="loading-spinner"></div>
                    Verifying...
                  </>
                ) : (
                  'Verify & Login'
                )}
              </button>

              <div className="login-actions">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setStep(1)}
                  disabled={loading}
                >
                  Change Phone Number
                </button>

                <button
                  type="button"
                  className="btn btn-outline"
                  onClick={handleResendCode}
                  disabled={loading || timeLeft > 0}
                >
                  {timeLeft > 0 ? `Resend in ${formatTime(timeLeft)}` : 'Resend Code'}
                </button>
              </div>
            </form>
          )}
        </div>

        {/* Footer */}
        <div className="login-footer">
          <p>
            Are you an employee? <Link to="/employee/login">Employee Login</Link>
          </p>
        </div>
      </div>

      <style jsx>{`
