import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useSocket } from '../../context/SocketContext';
import { ownerAPI, taskAPI, handleApiError } from '../../services/api';
import { toast } from 'react-toastify';
import { 
  Users, 
  CheckSquare, 
  MessageSquare, 
  BarChart3, 
  Plus,
  LogOut,
  Settings,
  Search
} from 'lucide-react';

const OwnerDashboard = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [dashboardData, setDashboardData] = useState(null);
  const [employees, setEmployees] = useState([]);
  const [tasks, setTasks] = useState([]);

  const { user, logout } = useAuth();
  const { isConnected } = useSocket();

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const [dashboardResponse, employeesResponse, tasksResponse] = await Promise.all([
        ownerAPI.getDashboard(),
        ownerAPI.getEmployees(),
        taskAPI.getTasks()
      ]);

      setDashboardData(dashboardResponse.data.data);
      setEmployees(employeesResponse.data.data);
      setTasks(tasksResponse.data.data);
    } catch (error) {
      console.error('Error loading dashboard data:', error);
      toast.error(handleApiError(error));
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    toast.info('Logged out successfully');
  };

  if (loading) {
    return (
      <div className="loading-overlay">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      {/* Header */}
      <header className="header">
        <div className="container">
          <div className="header-content">
            <div className="logo">Skipli</div>

            <div className="header-center">
              <div className="search-bar">
                <Search size={20} />
                <input type="text" placeholder="Search employees, tasks..." />
              </div>
            </div>

            <div className="header-right">
              <div className="connection-status">
                <div className={`status-dot ${isConnected ? 'connected' : 'disconnected'}`}></div>
                {isConnected ? 'Connected' : 'Connecting...'}
              </div>

              <div className="user-menu">
                <div className="user-menu-button">
                  <div className="user-avatar">
                    {user?.phoneNumber?.slice(-4) || 'MG'}
                  </div>
                  <span>Manager</span>
                </div>
                <div className="user-menu-dropdown">
                  <button className="dropdown-item">
                    <Settings size={16} />
                    Settings
                  </button>
                  <button className="dropdown-item" onClick={handleLogout}>
                    <LogOut size={16} />
                    Logout
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>

      <div className="dashboard-container">
        {/* Sidebar */}
        <nav className="sidebar">
          <div className="sidebar-nav">
            <button 
              className={`sidebar-nav-item ${activeTab === 'dashboard' ? 'active' : ''}`}
              onClick={() => setActiveTab('dashboard')}
            >
              <BarChart3 size={20} />
              Dashboard
            </button>
            <button 
              className={`sidebar-nav-item ${activeTab === 'employees' ? 'active' : ''}`}
              onClick={() => setActiveTab('employees')}
            >
              <Users size={20} />
              Employees
              <span className="badge badge-info">{employees.length}</span>
            </button>
            <button 
              className={`sidebar-nav-item ${activeTab === 'tasks' ? 'active' : ''}`}
              onClick={() => setActiveTab('tasks')}
            >
              <CheckSquare size={20} />
              Tasks
              <span className="badge badge-warning">{tasks.filter(t => t.status !== 'completed').length}</span>
            </button>
            <button 
              className={`sidebar-nav-item ${activeTab === 'messages' ? 'active' : ''}`}
              onClick={() => setActiveTab('messages')}
            >
              <MessageSquare size={20} />
              Messages
            </button>
          </div>
        </nav>

        {/* Main Content */}
        <main className="main-content">
          {activeTab === 'dashboard' && (
            <div className="dashboard-tab">
              <div className="page-header">
                <h1>Dashboard</h1>
                <p>Overview of your team and tasks</p>
              </div>

              {/* Stats Cards */}
              <div className="stats-grid">
                <div className="stat-card">
                  <div className="stat-icon employees">
                    <Users size={24} />
                  </div>
                  <div className="stat-content">
                    <h3>{dashboardData?.stats?.totalEmployees || 0}</h3>
                    <p>Total Employees</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon tasks">
                    <CheckSquare size={24} />
                  </div>
                  <div className="stat-content">
                    <h3>{dashboardData?.stats?.totalTasks || 0}</h3>
                    <p>Total Tasks</p>
                  </div>
                </div>

                <div className="stat-card">
                  <div className="stat-icon pending">
                    <CheckSquare size={24} />
                  </div>
                  <div className="stat-content">
                    <h3>{dashboardData?.stats?.pendingTasks || 0}</h3>
