import React, { useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { Users, MessageSquare, CheckSquare, Clock, Shield, Zap } from 'lucide-react';

const LandingPage = () => {
  const { isAuthenticated, user } = useAuth();
  const navigate = useNavigate();

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated && user) {
      const redirectPath = user.role === 'owner' 
        ? '/owner/dashboard' 
        : '/employee/dashboard';
      navigate(redirectPath, { replace: true });
    }
  }, [isAuthenticated, user, navigate]);

  return (
    <div className="landing-page">
      {/* Header */}
      <header className="header">
        <div className="container">
          <div className="header-content">
            <div className="logo">
              Skipli
            </div>
            <nav className="nav">
              <a href="#features" className="nav-link">Features</a>
              <a href="#how-it-works" className="nav-link">How it Works</a>
              <Link to="/owner/login" className="btn btn-outline btn-sm">
                Manager Login
              </Link>
              <Link to="/employee/login" className="btn btn-primary btn-sm">
                Employee Login
              </Link>
            </nav>
          </div>
        </div>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <div className="container">
          <div className="hero-content">
            <h1 className="hero-title">
              Real-Time Employee Task Management
            </h1>
            <p className="hero-subtitle">
              Streamline your team's productivity with our comprehensive task management solution. 
              Real-time communication, task tracking, and employee management in one powerful platform.
            </p>
            <div className="hero-actions">
              <Link to="/owner/login" className="btn btn-primary btn-lg">
                Get Started as Manager
              </Link>
              <Link to="/employee/login" className="btn btn-outline btn-lg">
                Employee Access
              </Link>
            </div>
          </div>
          <div className="hero-image">
            <div className="hero-mockup">
              <div className="mockup-header">
                <div className="mockup-dots">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
              <div className="mockup-content">
                <div className="mockup-sidebar">
                  <div className="sidebar-item active">Dashboard</div>
                  <div className="sidebar-item">Employees</div>
                  <div className="sidebar-item">Tasks</div>
                  <div className="sidebar-item">Messages</div>
                </div>
                <div className="mockup-main">
                  <div className="task-card">
                    <div className="task-header">
                      <h4>Complete Project Proposal</h4>
                      <span className="badge badge-warning">In Progress</span>
                    </div>
                    <p>Due: Tomorrow</p>
                  </div>
                  <div className="task-card">
                    <div className="task-header">
                      <h4>Review Code Changes</h4>
                      <span className="badge badge-success">Completed</span>
                    </div>
                    <p>Due: Today</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section id="features" className="features">
        <div className="container">
          <div className="section-header">
            <h2>Powerful Features</h2>
            <p>Everything you need to manage your team effectively</p>
          </div>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">
                <Users size={32} />
              </div>
              <h3>Employee Management</h3>
              <p>Add, edit, and manage employee profiles with detailed information and role assignments.</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <CheckSquare size={32} />
              </div>
              <h3>Task Tracking</h3>
              <p>Create, assign, and track tasks with real-time status updates and progress monitoring.</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <MessageSquare size={32} />
              </div>
              <h3>Real-time Chat</h3>
              <p>Instant messaging between managers and employees with typing indicators and notifications.</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <Clock size={32} />
              </div>
              <h3>Live Updates</h3>
              <p>Real-time synchronization across all connected devices for instant collaboration.</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <Shield size={32} />
              </div>
              <h3>Secure Access</h3>
              <p>Multi-factor authentication with SMS and email verification for enhanced security.</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <Zap size={32} />
              </div>
              <h3>Fast & Responsive</h3>
              <p>Lightning-fast performance with modern web technologies and responsive design.</p>
            </div>
          </div>
        </div>
      </section>

      {/* How it Works Section */}
      <section id="how-it-works" className="how-it-works">
        <div className="container">
          <div className="section-header">
            <h2>How It Works</h2>
            <p>Get started in minutes with our simple setup process</p>
          </div>
          <div className="steps">
            <div className="step-tabs">
              <div className="step-tab active" data-tab="managers">
                For Managers
              </div>
              <div className="step-tab" data-tab="employees">
                For Employees
              </div>
            </div>

            <div className="step-content active" id="managers">
              <div className="steps-grid">
                <div className="step">
                  <div className="step-number">1</div>
                  <h3>Phone Verification</h3>
                  <p>Enter your phone number and verify with SMS code</p>
                </div>
                <div className="step">
                  <div className="step-number">2</div>
                  <h3>Add Employees</h3>
                  <p>Create employee profiles and send setup invitations</p>
                </div>
                <div className="step">
                  <div className="step-number">3</div>
                  <h3>Manage & Communicate</h3>
                  <p>Assign tasks, track progress, and chat in real-time</p>
                </div>
              </div>
            </div>

            <div className="step-content" id="employees">
              <div className="steps-grid">
                <div className="step">
                  <div className="step-number">1</div>
                  <h3>Receive Invitation</h3>
                  <p>Get setup email from your manager with account details</p>
                </div>
                <div className="step">
                  <div className="step-number">2</div>
                  <h3>Set Up Account</h3>
                  <p>Create username and password for secure access</p>
                </div>
                <div className="step">
                  <div className="step-number">3</div>
                  <h3>Start Working</h3>
                  <p>View tasks, update progress, and communicate with your manager</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta">
        <div className="container">
          <div className="cta-content">
            <h2>Ready to Transform Your Team Management?</h2>
            <p>Join thousands of teams already using Skipli to boost productivity and streamline communication.</p>
            <div className="cta-actions">
              <Link to="/owner/login" className="btn btn-primary btn-lg">
                Start Managing Today
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>Skipli</h3>
              <p>Real-time employee task management made simple.</p>
            </div>
            <div className="footer-section">
              <h4>Quick Links</h4>
              <ul>
                <li><Link to="/owner/login">Manager Login</Link></li>
                <li><Link to="/employee/login">Employee Login</Link></li>
              </ul>
            </div>
            <div className="footer-section">
              <h4>Support</h4>
              <ul>
                <li><a href="mailto:engineering@skiplinow.com">Contact Support</a></li>
              </ul>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2024 Skipli. All rights reserved.</p>
          </div>
        </div>
      </footer>

      <style jsx>{`
        .landing-page {
          min-height: 100vh;
        }

        .hero {
          padding: 80px 0;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
        }

        .hero-content {
          max-width: 600px;
          margin-bottom: 60px;
        }

        .hero-title {
          font-size: 3.5rem;
          font-weight: 700;
          margin-bottom: 20px;
          line-height: 1.2;
        }

        .hero-subtitle {
          font-size: 1.25rem;
          margin-bottom: 40px;
          opacity: 0.9;
        }

        .hero-actions {
          display: flex;
          gap: 20px;
          flex-wrap: wrap;
        }

        .hero-image {
          display: flex;
          justify-content: center;
        }

        .hero-mockup {
          background: white;
          border-radius: 12px;
          box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
          overflow: hidden;
          width: 500px;
