import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { employeeAPI, handleApiError } from '../../services/api';
import { toast } from 'react-toastify';
import { Mail, Lock, Eye, EyeOff, LogIn, ArrowLeft } from 'lucide-react';

const EmployeeLogin = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [step, setStep] = useState('login'); // 'login' or 'setup'
  const [setupToken, setSetupToken] = useState('');

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!formData.email || !formData.password) {
      toast.error('Please fill in all fields');
      return;
    }

    try {
      setLoading(true);
      const response = await employeeAPI.login(formData);

      if (response.data.requiresSetup) {
        // Employee needs to complete setup
        setSetupToken(response.data.setupToken);
        setStep('setup');
        toast.info('Please complete your account setup');
      } else {
        // Login successful
        login(response.data.data, 'employee');
        toast.success('Login successful!');
        navigate('/employee/dashboard');
      }
    } catch (error) {
      console.error('Login error:', error);
      toast.error(handleApiError(error));
    } finally {
      setLoading(false);
    }
  };

  const handleSetup = async (e) => {
    e.preventDefault();

    const setupData = new FormData(e.target);
    const data = {
      name: setupData.get('name'),
      password: setupData.get('password'),
      confirmPassword: setupData.get('confirmPassword'),
      department: setupData.get('department'),
      role: setupData.get('role')
    };

    if (!data.name || !data.password || !data.confirmPassword || !data.department || !data.role) {
      toast.error('Please fill in all fields');
      return;
    }

    if (data.password !== data.confirmPassword) {
      toast.error('Passwords do not match');
      return;
    }

    if (data.password.length < 6) {
      toast.error('Password must be at least 6 characters long');
      return;
    }

    try {
      setLoading(true);
      const response = await employeeAPI.completeSetup({
        ...data,
        setupToken
      });

      login(response.data.data, 'employee');
      toast.success('Account setup completed successfully!');
      navigate('/employee/dashboard');
    } catch (error) {
      console.error('Setup error:', error);
      toast.error(handleApiError(error));
    } finally {
      setLoading(false);
    }
  };

  const handleBackToHome = () => {
    navigate('/');
  };

  const handleBackToLogin = () => {
    setStep('login');
    setSetupToken('');
  };

  return (
    <div className="employee-login">
      <div className="login-container">
        <div className="login-card">
          <div className="login-header">
            <button className="back-button" onClick={handleBackToHome}>
              <ArrowLeft size={20} />
              Back to Home
            </button>

            <div className="logo">Skipli</div>

            {step === 'login' ? (
              <>
                <h1>Employee Login</h1>
                <p>Sign in to access your tasks and messages</p>
              </>
            ) : (
              <>
                <h1>Complete Setup</h1>
                <p>Please complete your account setup</p>
              </>
            )}
          </div>

          {step === 'login' ? (
            <form onSubmit={handleLogin} className="login-form">
              <div className="form-group">
                <label htmlFor="email">Email Address</label>
                <div className="input-wrapper">
                  <Mail className="input-icon" size={20} />
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    placeholder="Enter your email"
                    required
                    disabled={loading}
                  />
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="password">Password</label>
                <div className="input-wrapper">
                  <Lock className="input-icon" size={20} />
                  <input
                    type={showPassword ? 'text' : 'password'}
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    placeholder="Enter your password"
                    required
                    disabled={loading}
                  />
                  <button
                    type="button"
                    className="password-toggle"
                    onClick={() => setShowPassword(!showPassword)}
                    disabled={loading}
                  >
                    {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                  </button>
                </div>
              </div>

              <button 
                type="submit" 
                className="login-button"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <div className="spinner"></div>
                    Signing in...
                  </>
                ) : (
                  <>
                    <LogIn size={20} />
                    Sign In
                  </>
                )}
              </button>

              <div className="login-help">
                <p>
                  Don't have an account? Contact your manager to get access.
                </p>
                <p>
                  Having trouble? <a href="#" onClick={(e) => { e.preventDefault(); toast.info('Contact your manager for support'); }}>Get help</a>
                </p>
              </div>
            </form>
          ) : (
            <form onSubmit={handleSetup} className="setup-form">
              <div className="setup-header">
                <button type="button" className="back-link" onClick={handleBackToLogin}>
                  <ArrowLeft size={16} />
                  Back to Login
                </button>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="name">Full Name</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    placeholder="Enter your full name"
                    required
                    disabled={loading}
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="department">Department</label>
                  <select
                    id="department"
                    name="department"
                    required
                    disabled={loading}
                  >
                    <option value="">Select Department</option>
                    <option value="Engineering">Engineering</option>
                    <option value="Marketing">Marketing</option>
                    <option value="Sales">Sales</option>
                    <option value="Support">Support</option>
                    <option value="HR">Human Resources</option>
                    <option value="Finance">Finance</option>
                    <option value="Operations">Operations</option>
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="role">Role</label>
                  <input
                    type="text"
                    id="role"
                    name="role"
                    placeholder="e.g. Software Developer"
                    required
                    disabled={loading}
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="password">Create Password</label>
                  <div className="input-wrapper">
                    <Lock className="input-icon" size={20} />
                    <input
                      type={showPassword ? 'text' : 'password'}
                      id="password"
                      name="password"
                      placeholder="Create a secure password"
                      required
                      disabled={loading}
                      minLength={6}
                    />
                    <button
                      type="button"
                      className="password-toggle"
                      onClick={() => setShowPassword(!showPassword)}
                      disabled={loading}
                    >
                      {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                    </button>
                  </div>
                  <div className="field-hint">
                    Password must be at least 6 characters long
                  </div>
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="confirmPassword">Confirm Password</label>
                  <div className="input-wrapper">
                    <Lock className="input-icon" size={20} />
                    <input
                      type={showPassword ? 'text' : 'password'}
                      id="confirmPassword"
                      name="confirmPassword"
                      placeholder="Confirm your password"
                      required
                      disabled={loading}
                      minLength={6}
                    />
                  </div>
                </div>
              </div>

              <button 
                type="submit" 
                className="setup-button"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <div className="spinner"></div>
                    Setting up...
                  </>
                ) : (
                  'Complete Setup'
                )}
              </button>
            </form>
          )}
        </div>
      </div>

      <style jsx>{`
